import React, { useEffect, useState } from "react";
import "../../App.css";
import Fade from 'react-reveal/Fade';
import Typist from "react-typist";
import "../Styles/Experience.css";
import styled from "styled-components";

const ButtonGroup = styled.div`
display: flex;
  margin-left:250px;
  margin-right:250px;
`

const Button = styled.button`
  font: 1.3rem Inconsolata, monospace;
  background-color: green;
  color: white;
  font-size: 20px;
  padding: 10px 60px;
  border-radius: 5px;
  margin: 10px 0px;
  display:block;
  
  
  cursor: pointer;
  &:disabled {
    color: grey;
    opacity: 0.7;
    cursor: default;
  }
`;



class Experience extends React.Component {
  state = {
    name: 'Anand',
  };
  changeState = () => {
    this.setState({
      name: 'Sana',
    });
  };
  render(){
    const state = this.state;
  return (
    
      <div className="exp-container" > 
          <Typist className="exp-header">
          
          /experience
          </Typist>

          <ButtonGroup>
            <Button onClick={this.changeState}> Florida Polytechnic University | Dept. of Computer Science </Button>
            <Button> Florida Polytechnic University | Student Affairs </Button>
            <Button> CNP Agency </Button>
            <Button> Florida Polytechnic University | Office of Admissions </Button>
          </ButtonGroup>
          <p>{text}</p>
        
      </div>
    
  );}
}
export default Experience;
