import React, { useEffect, useState } from "react";
import "../../App.css";
import Fade from 'react-reveal/Fade';
import Typist from "react-typist";
import "../Styles/Experience.css";

class Experience extends React.Component {

  state = {
    text: 'Click to see!',
    clicked: false
  };

  onClickButton1 = () => {
    this.setState({
      clicked: true,
      text: ' Developing and implementing NPC AI behavior for Virtual Bariatric Endoscopy Simulator (ViBE) using finite state machines and Unity.'
    });
  }

  onClickButton2 = () => {
    this.setState({
      text: ' I oversee 40 Residents and directly mentor 5 Resident Assistants. I also serve on a 24/7 on-call rotation to approximately 800 residential students, where I am the first response to a wide array of on-campus emergencies. I also plan events for 800+ Students with a group of Resident Assistants, as well as design flyers for events using Canva.'
    });
  }

  onClickButton3 = () => {
    this.setState({
      text: 'Worked with the Media and Analytics team to implement on-page and technical SEO recommendations. Programmed alongside the Web Development team to implement client web page changes. Utilized Google Analytics, Screaming Frog, WordPress, React.JS, and Docker.'
    });
  }

  onClickButton4 = () => {
    this.setState({
      text: 'Working alongside Vice Provost for Enrollment to find technical errors and improve the SEO of the Florida Poly Website. Improved SEO score from 87-92 (out of 100). Grew organic web traffic by 29.1%.'
    });
  }

  onClickButton5 = () => {
    this.setState({
      text: ' Managed the Admissions Center welcome desk and phone line, maintained the organization of university merchandise, provided guided campus tours to groups of prospective students to the university, collaborated with the admissions director to organize admissions events and activities, and lead a team of 3 people to collect data to build a Chatbot.'
    });
  }
  render(){
    const { clicked } = this.state;
    const buttonStyle = clicked ? { backgroundColor: 'red', color: 'white' } : {};
  return (
    
      <div className="exp-container" > 
          <Typist className="exp-header">
          
          /experience
          </Typist>
        <div style={{ display: "flex" }}>
        <button style={buttonStyle}  onClick={() => this.onClickButton1()}>
        Florida Polytechnic University
        </button>
        <button style={{ width: "300px", height: "150px",marginLeft: "auto", marginRight: "auto", fontSize:"22px"}} onClick={() => this.onClickButton2()}>
       Florida Polytechnic University Student Affairs
        </button>
        <button style={{ width: "300px", height: "150px",marginLeft: "auto", marginRight: "auto", marginBottom: "20px", fontSize:"22px"}}onClick={() => this.onClickButton3()}>
       CNP Agency
        </button>
        <button style={{ width: "300px", height: "150px",marginLeft: "auto", marginRight: "auto", fontSize:"22px"}}onClick={() => this.onClickButton4()}>
        Florida Polytechnic University Office of Admissions 
        </button>
        </div>
        <div style={{textAlign: "center", fontSize: "18px", marginTop: "5px"}}>{this.state.text}</div>    
      </div>
    
  );}
}
export default Experience;
