import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../App.css";

import "./HeroSection.css";
import "./Navbar.css";

import Typewriter from "typewriter-effect";

import { Parallax, ParallaxProvider } from "../../src";
import { Element } from "../Element/Element";
import { Container } from "../Container";
import { ScrollContainer } from "../ScrollContainer";
import styles from "./Parallax.module.scss";
import { useRef } from "react";
import { CSSEffect } from "parallax-controller";

function HeroSection() {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);

  return (
    <div className="hero-container">
      {/* <video src="/videos/opt3.mp4" autoPlay loop muted /> */}
      <p id="rcorners2">
        <div className="menu-icon" onClick={handleClick}>
          <i className={click ? "fas fa-times" : "fas fa-bars"} />
        </div>
        <ul className={click ? "nav-menu active" : "nav-menu"}>
          <li className="nav-item">
            <Link to="/" className="nav-links">
              Home
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/projects" className="nav-links">
              Projects
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/about-me" className="nav-links">
              About Me
            </Link>
          </li>

          <li>
            <Link to="/contact-me" className="nav-links-mobile">
              Contact me
            </Link>
          </li>
        </ul>
      </p>
      <h1>
        <Typewriter
          onInit={(typewriter) => {
            typewriter.typeString("> Hello, Nelson here.\n").start();
          }}
        />
      </h1>
      <h2>
        <Typewriter
          onInit={(typewriter) => {
            typewriter
              .pauseFor(3700)
              .typeString("> Sometimes I code things.\n")
              .start();
          }}
        />
      </h2>
    </div>
  );
}

export default HeroSection;
