import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Parallax, useParallax } from "react-scroll-parallax";
import { ParallaxBanner } from "react-scroll-parallax";
import { ParallaxProvider } from "react-scroll-parallax";
import "../App.css";

import "./HeroSection.css";
import "./Navbar.css";

import Typewriter from "typewriter-effect";

function Component() {
  const { ref } = useParallax < HTMLDivElement > { speed: 10 };
  return <div ref={ref} />;
}

function HeroSection() {
  const amount = 2;
  const offA = -50;
  const offB = 50;
  const unit = "%";
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  const TranslateY = () => (
    <Parallax translateY={[-20, 10]}>
      <div className="my-thing" />
    </Parallax>
  );

  return (
    <div className="hero-container">
      {/* <video src="/videos/opt3.mp4" autoPlay loop muted /> */}
      <ParallaxBanner
        speed={10}
        translateY={[`${offA * 5}${unit}`, `${offB * 5}${unit}`]}
      >
        <p id="rcorners2">
          <div className="menu-icon" onClick={handleClick}>
            <i className={click ? "fas fa-times" : "fas fa-bars"} />
          </div>
          <ul className={click ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/" className="nav-links">
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/projects" className="nav-links">
                Projects
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/about-me" className="nav-links">
                About Me
              </Link>
            </li>

            <li>
              <Link to="/contact-me" className="nav-links-mobile">
                Contact me
              </Link>
            </li>
          </ul>
        </p>
      </ParallaxBanner>
      <h1>
        <Typewriter
          onInit={(typewriter) => {
            typewriter.typeString("> Hello, Nelson here.\n").start();
          }}
        />
      </h1>
      <h2>
        <Typewriter
          onInit={(typewriter) => {
            typewriter
              .pauseFor(3700)
              .typeString("> Sometimes I code things.\n")
              .start();
          }}
        />
      </h2>
    </div>
  );
}

export default HeroSection;
