import React, { useEffect, useState } from "react";
import "../../App.css";
import Fade from 'react-reveal/Fade';
import Typist from "react-typist";
import "../Styles/Experience.css";
import styled from "styled-components";

const ButtonGroup = styled.div`
display: flex;
  margin-left:250px;
  margin-right:250px;
`

const Button = styled.button`
  font: 1.3rem Inconsolata, monospace;
  background-color: green;
  color: white;
  font-size: 20px;
  padding: 10px 60px;
  border-radius: 5px;
  margin: 10px 0px;
  display:block;
  
  
  cursor: pointer;
  &:disabled {
    color: grey;
    opacity: 0.7;
    cursor: default;
  }
`;

let [unitState, setUnitState] = React.useState('°C')

class Experience extends React.Component {
  
  render(){
    
  return (
    
      <div className="exp-container" > 
          <Typist className="exp-header">
          
          /experience
          </Typist>

          <ButtonGroup>
            <Button onClick={() => setUnitState('°C')}> Florida Polytechnic University | Dept. of Computer Science </Button>
            <Button onClick={() => setUnitState('°F')}> Florida Polytechnic University | Student Affairs </Button>
            <Button> CNP Agency </Button>
            <Button> Florida Polytechnic University | Office of Admissions </Button>
          </ButtonGroup>
          <div>{unitState}</div>
        
      </div>
    
  );}
}
export default Experience;
